import React, { useEffect, useState } from "react";
import "./App.css";


const Stock = () => {
  const APP_KEY = "A9SKKB7RBL4A5ZKS";

  const [search, setSearch] = useState("");
  const [Stock, setStock] = useState([]);
  const [query, setQuery] = useState("TCS");

  useEffect(() => {
    getStock();
  }, [query]);

  const getStock = async() => {
    const response = await fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${query}&apikey=${APP_KEY}`);
    const data = await response.json();
    setStock(data);
    console.log(data);
  }
  const updateSearch = e => {
    setSearch(e.target.value);
  };

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("");
  };

  return (
    <div className="Stock">
     <main>
      <form onSubmit={getSearch} className="search-form">
        <input
          className="search-bar"
          type="text"
          value={search}
          onChange={updateSearch}
        />
        <button className="search-button" type="submit">
          Search
        </button>
      </form>
      {(typeof Stock.GlobalQuote != "undefined") ? (
        <div className="Info">
            <p>Hello</p>
        </div>
        ) : ('')}
     </main>
    </div>
  );
};

export default Stock;
