import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';


const IndiaStates = () => {
    const [provinces, setProvinces] = useState([]);

    useEffect(() => {
        getProvinces();
      }, []);

    const getProvinces = async() => {
        const response = await fetch(`https://covid19.mathdro.id/api/countries/india/recovered`);
        const data = await response.json();
        setProvinces(data);
        console.log(data);
    }

    return (
        <div className="provinces container">
        <Table responsive striped bordered hover variant="dark" >
            <thead>
                <tr>
                    <th>State</th>
                    <th>Confirmed</th>
                    <th>Recovered</th>
                    <th>Active</th>
                    <th>Deaths</th>
                </tr>
            </thead>
            <tbody>
                {provinces.map(province => (
                    <tr>
                        <td>{province.provinceState}</td>
                        <td>{province.confirmed}</td>
                        <td>{province.recovered}</td>
                        <td>{province.active}</td>
                        <td>{province.deaths}</td>
                    </tr>
                ))}
            </tbody>
        </Table>
        </div>
    )
}


export default IndiaStates;
