import React, { useEffect, useState } from "react";
import CardDeck from 'react-bootstrap/CardDeck';
import Card from 'react-bootstrap/Card';

const Country = () => {
    const [search, setSearch] = useState("");
    const [cases, setCases] = useState([]);
    const [query, setQuery] = useState("india");

  useEffect(() => {
    getCases();
  }, [query]);

  const getCases = async() => {
    const response = await fetch(`https://covid19.mathdro.id/api/countries/${query}`);
    const data = await response.json();
    setCases(data);
    console.log(data);
    console.log(data.confirmed);
  }
  const updateSearch = e => {
    setSearch(e.target.value);
  };
  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("");
  };

  return (
    <div className="Country">
     <main>
      <h3>#Made using <a href="https://covid19.mathdro.id/api/">covid19.mathdro.id/api</a></h3>
      <form onSubmit={getSearch} className="search-form">
        <input
          className="search-bar"
          type="text"
          value={search}
          onChange={updateSearch}
        />
        <button className="search-button" type="submit">
          Search
        </button>
      </form>
      <div className="container Country">
      {(typeof cases != "undefined") ? (
        <CardDeck>
          <Card>
            <Card.Body>
              <Card.Title>Total Confirmed Cases</Card.Title>
              <Card.Text>{cases.confirmed.value}</Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
                This card has supporting text below as a natural lead-in to additional
                content.{' '}
              </Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
                This is a wider card with supporting text below as a natural lead-in to
                additional content. This card has even longer content than the first to
                show that equal height action.
              </Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated 3 mins ago</small>
            </Card.Footer>
          </Card>
        </CardDeck>
        ) : ('')}
      </div>
     </main>
    </div>
  );
};

export default Country;
