import React, { useEffect, useState } from "react";
import "./App.css";

const App = () => {
  const APP_KEY = "d9f018ab094f5f7a34df0ec41f79c368";

 const [weather, setWeather] = useState([]);
 const [search, setSearch] = useState("");
 const [query, setQuery] = useState("Mumbai");

 useEffect(() => {
   getWeather();
 }, [query]);

 //requesting and getting data from another website
 const getWeather = async () => {
   const response = await fetch(
     `https://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=${APP_KEY}`
   );
   const data = await response.json();
   setWeather(data.hits);
   console.log(data.hits);
 };
 const updateSearch = e => {
   setSearch(e.target.value);
 };
 const getSearch = e => {
   e.preventDefault();
   setQuery(search);
   setSearch("");
 };

 return (
   //search form and mapping through each recipe
   <div className="App">
     <form onSubmit={getSearch} className="search-form">
       <input
         className="search-bar"
         type="text"
         value={search}
         onChange={updateSearch}
       />
       <button className="search-button" type="submit">
         Search
       </button>
     </form>
     <div>
         <div className="location-box">
           Mumbai
         </div>
     </div>
   </div>
 );
};

export default App;
