import React, { useState } from "react";
import "./App.css";


const api = {
  key: "d9f018ab094f5f7a34df0ec41f79c368",
}
function App(){
 const [weather, setWeather] = useState([]);
 const [query, setQuery] = useState("");

 //requesting and getting data from another website
 const search = async () => {
  if (evt.key === "Enter") {
   const response = await fetch(
     `https://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=${APP_KEY}`
   );
   const data = await response.json();
   setWeather(data.hits);
   setQuery('');
   console.log(data.hits);
 }
};

 return (
   //search form and mapping through each recipe
   <div className="App">
    <div className="search-box">
        <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
        />
    </div>
     <div>
         <div className="location-box">
           Mumbai
         </div>
     </div>
   </div>
 );
};

export default App;
