import React, { useEffect, useState } from "react";
import CardDeck from 'react-bootstrap/CardDeck';
import Card from 'react-bootstrap/Card';
import CountUp from 'react-countup';

const Country = () => {
    const [search, setSearch] = useState("");
    const [lastUpdate, setLastUpdate] = useState([]);
    const [confirmed, setConfirmed] = useState([]);
    const [recovered, setRecovered] = useState([]);
    const [deaths, setDeaths] = useState([]);
    const [query, setQuery] = useState("india");

  useEffect(() => {
    getConfirmed();
    getRecovered();
    getDeaths();
    getLastUpdate();
  }, [query]);

  const getConfirmed = async() => {
    const response = await fetch(`https://covid19.mathdro.id/api/countries/${query}`);
    const data = await response.json();
    setConfirmed(data.confirmed);
    console.log(data);
  }
  const getRecovered = async() => {
    const response = await fetch(`https://covid19.mathdro.id/api/countries/${query}`);
    const data = await response.json();
    setRecovered(data.recovered);
    console.log(data);
  }
  const getDeaths = async() => {
    const response = await fetch(`https://covid19.mathdro.id/api/countries/${query}`);
    const data = await response.json();
    setDeaths(data.deaths);
    console.log(data);
  }
  const getLastUpdate = async() => {
    const response = await fetch(`https://covid19.mathdro.id/api/countries/${query}`);
    const data = await response.json();
    setLastUpdate(data.lastUpdate);
    console.log(data);
  }
  const updateSearch = e => {
    setSearch(e.target.value);
  };
  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("");
  };

  return (
    <div className="Country">
     <main>
      <h3>#Made using <a href="https://covid19.mathdro.id/api/">covid19.mathdro.id/api</a></h3>
      <form onSubmit={getSearch} className="search-form">
        <input
          className="search-bar"
          type="text"
          value={search}
          onChange={updateSearch}
        />
        <button className="search-button" type="submit">
          Search
        </button>
      </form>
      <div className="container Country">
        <div>{ confirmed.value }</div>
        <CardDeck>
          <Card>
            <Card.Body>
              <Card.Title>Total Confirmed Cases:</Card.Title>
              <CountUp start={0} end={confirmed.value} duration={2.75} separator="," />
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated {new Date(lastUpdate).toDateString()}</small>
            </Card.Footer>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
              { recovered.value }
              </Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated {new Date(lastUpdate).toDateString()}</small>
            </Card.Footer>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Card title</Card.Title>
              <Card.Text>
              { deaths.value }
              </Card.Text>
            </Card.Body>
            <Card.Footer>
              <small className="text-muted">Last updated {new Date(lastUpdate).toDateString()}</small>
            </Card.Footer>
          </Card>
        </CardDeck>
      </div>
     </main>
    </div>
  );
};

export default Country;
